IF NOT EXISTS( SELECT * FROM sys.tables [table] INNER JOIN sys.columns [column] ON [table].object_id = [column].object_id WHERE [table].name = 'ChatMessage' AND	[column].name = 'UsrK')     BEGIN ALTER TABLE dbo.ChatMessage ADD UsrK     int              NULL EXECUTE sp_addextendedproperty N'MS_Description', N'UsrK of the posting user', N'SCHEMA', N'dbo', N'TABLE', N'ChatMessage', N'COLUMN', N'UsrK' END
IF NOT EXISTS( SELECT * FROM sys.tables [table] INNER JOIN sys.columns [column] ON [table].object_id = [column].object_id WHERE [table].name = 'ChatMessage' AND	[column].name = 'RoomGuid') BEGIN ALTER TABLE dbo.ChatMessage ADD RoomGuid uniqueidentifier NULL EXECUTE sp_addextendedproperty N'MS_Description', N'Room posted to',           N'SCHEMA', N'dbo', N'TABLE', N'ChatMessage', N'COLUMN', N'RoomGuid' END
